import React from 'react'
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Typography, AppBar, CssBaseline, Grid, Toolbar} from '@mui/material'
import DashboardIcon from '@mui/icons-material/Dashboard';
import WidgetCard from './components/WidgetCard';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import WidgetAlarm from './components/WidgetAlarm';
import WidgetWeather from './components/WidgetWeather';
import Widget4 from './components/Widget4';
import Widget5 from './components/Widget5';
import Widget6 from './components/Widget6';

import IconButton from '@mui/material/IconButton';


import { useTheme } from '@mui/material/styles';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';

const ColorModeContext = React.createContext({ toggleColorMode: () => {} });


const App = () => {

    const theme = useTheme();
    const colorMode = React.useContext(ColorModeContext);
    return (

    
        <IconButton sx={{ ml: 1 }} onClick={colorMode.toggleColorMode} color="inherit">
          {theme.palette.mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
        </IconButton>
      
    );
  }
  
  export function ToggleColorMode() {
    const [mode, setMode] = React.useState('light');
    const colorMode = React.useMemo(
      () => ({
        toggleColorMode: () => {
          setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
        },
      }),
      [],
    );
  
    const theme = React.useMemo(
      () =>
        createTheme({
          palette: {
            mode,
          },
        }),
      [mode],
    );


  return (
        <>
    <ColorModeContext.Provider value={colorMode}>
        <ThemeProvider theme={theme}>
            <CssBaseline/>
            <AppBar position="relative">
                <Toolbar>
                    <DashboardIcon />
                    <Typography variant='h6'>
                        Widget Dashboard
                    </Typography>
                </Toolbar>
            </AppBar>                  
        </ThemeProvider>
        <main>
            <Container maxWidth="xxl">
                <Box sx={{ bgcolor: '#e6e6e6', height: '100%', padding: 5, margin: 5}}>
                    <Box sx={{ flexGrow: 1 }}>
                        <Grid container spacing={5} columns={16}>

                            <Grid item lg={8} md={8}>
                                
                                    <WidgetCard>

                                    </WidgetCard>
                                
                            </Grid>
                            <Grid item lg={8} md={4}>
                            
                                    <WidgetAlarm>

                                    </WidgetAlarm>
                               
                            </Grid>
                            <Grid item lg={5} md={4}>
                            
                                    <WidgetWeather>

                                    </WidgetWeather>
                            </Grid>
                            <Grid item lg={6} md={4}>
                       
                                    <Widget4>

                                    </Widget4>
                             
                            </Grid>
                            <Grid item lg={5} md={8}>
                        
                                    <Widget5>

                                    </Widget5>

                            </Grid>
                            <Grid item lg={18} md={4}>

                                    <Widget6>

                                    </Widget6>
                     
                            </Grid>
                        </Grid>
                    </Box>
                </Box> 
            </Container>
        </main>
        </ColorModeContext.Provider>
        </>
  )
}

export default App;
